mapper.select:
	SELECT p_code FROM problems where p_id IN (    
      SELECT p_id FROM problems 
         where p_unit = #{p_unit} 
            and p_subject = #{p_subject} 
            and p_source = #{p_source} 
            and p_difficulty = #{p_difficulty}
      	)
	ORDER BY RAND()
	LIMIT #{p_size}

SELECT DISTINCT p_unit FROM problems where p_source in (SELECT DISTINCT p_source 
From p_source = #{p_source}
WHere p_subject = #{p_subject});

SELECT DISTINCT p_difficulty FROM problems where p_unit in 
(SELECT DISTINCT p_unit 
From p_unit = #{p_unit}
WHere p_source = #{p_source}
and p_subject = p_subject);
